INSERT INTO `wp_news` (`id`, `gambar`, `judul`, `deskripsi`, `kategori`, `date_added`, `date_modified`) VALUES
(1, '1679890085Picture1.jpg', 'Penandatanganan Deklarasi Merdeka Belajar, Kampus Merdeka Telkom University bersama Kementrian Pendidikan Kebudayaan, LLDIKTI IV dan Mitra Industri', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Pada hari Jum’at, 6 Maret 2020 CeLOE (Center of eLearning and Open Education) ikut berkontribusi dalam persiapan dan pelaksanaan kegiatan Deklarasi Merdeka Belajar, Kampus Merdeka Telkom University bersama Kemendikbud dan Mitra Industri. Kegiatan ini diawali dengan sambutan Rektor Telkom University Prof. Dr. Adiwijaya dan kemudian dilanjutkan dengan video conference realtime oleh Plt. Direktur Jenderal Pendidikan Tinggi, Kemendikbud yaitu Prof. Ir. Nizam. M.Sc., Ph.D yang memaparkan tentang Merdeka Belajar, Kampus Merdeka yang disaksikan langsung oleh seluruh jajaran tamu undangan yang hadir. Pada sesi selanjutnya Prof. Dr. Uman Suherman AS, M.Pd. selaku Kepala LLDIKTI Wilayah IV menyampaikan sambutan dan menyampaikan beberapa hal penting terkait persiapan dalam perwujudan Merdeka Belajar, Kampus Merdeka khususnya dari sumber daya pengajar sebagai pelaku utama untuk mengarahkan dalam pelaksanaan program tersebut. Dalam video yang ditampilkan Telkom University sebagai Perguruan Tinggi Swasta (PTS) terbaik di Indonesia telah mengimplementasikan beberapa program sesuai dengan deklarasi Merdeka Belajar, Kampus Merdeka. Hal tersebut ditekankan melalui pemaparan Dr. Dadan Rahadian selaku Wakil Rektor Bidang Akademik dengan beberapa persiapan dan program-program mendukung lainnya. Puncak dari kegiatan hari ini adalah penandatangan deklarasi Merdeka Belajar, Kampus Merdeka antara Rektor Telkom University, Mitra Industri, Wakil Rektor Bidang Akademik, dan Dekan serta jajaran Ketua Program Studi di Universitas Telkom. Kegiatan ditutup dengan foto bersama-sama dengan spirit Merdeka Belajar, Kampus Merdeka.</p>', 'Dokumentasi', '2023-04-30 02:26:01', '2023-04-30 02:26:01'),
(3, '1679901695Picture2.jpg', 'Sharing Session CeLOE bersama Dosen-Dosen di Universitas Telkom', '<p>Dalam empat minggu terakhir, CeLOE (Center of eLearning and Open Education) mendapat undangan sharing session dari fakultas-fakultas di Universitas Telkom yang dimulai dari Fakultas Industri Kreatif (FIK), Fakultas Komunikasi dan Bisnis (FKB), Fakultas Ilmu Terapan (FIT), Fakultas Teknik Elektro (FTE), Fakultas Ekonomi Bisnis (FEB) dan kemudian yang terakhir mengundang yaitu Fakultas Rekayasa Industri (FRI). Sharing session ini merupakan kesempatan bagi CeLOE beserta seluruh dosen fakultas untuk meningatkan motivasi dan produksi konten video eLearning yang lebih baik lagi. Sharing session ini dimulai dengan penjelasan mengenai studio CeLOE dan CeLOE platform dimana terdapat 14 studio yang siap digunakan untuk shooting, fungsionalitas Course Development System (CDS) dan Learning Management System (LMS).&nbsp; Setelah itu dianjutkan dengan membahas pengimplementasian blended learning pada 44 mata kuliah terpilih yang tentunya perlu adanya persiapan baik teknis maupun non teknis. Untuk itu tim CeLOE memberikan penjelasan mengenai “Shooting itu mudah” melalui arahan-arahan yang dapat meningkatkan produksi content CeLOE sesuai dengan terget yang diinginkan. Pada akhir sharing session ini dibuka tanya jawab dan masukan-masukan serta arahan yang membangun.</p>', 'Artikel', '2023-03-27 00:21:35', '2023-03-27 00:21:35'),
(4, '1679901807Picture3.jpg', 'Kunjungan Pusdikat Tenaga Teknis Pendidikan dan Keagamaan Badan Litbang dan Diklat Kementrian Agama bersama CeLOE', '<p>Pusdikat Tenaga Teknis Pendidikan dan Keagamaan Badan Litbang dan Diklat Kementrian Agama telah melaksanakan kunjungan ke CeLOE (Center of eLearning and Open Education) Universitas Telkom pada hari Kamis, 20 Februari 2020 di Ruang Rapat Pimpinan Lantai 5, Gedung Rektorat di Universitas Telkom. Dalam kegiatan kunjungan ini, Kementrian Agama ingin berdiskusi dan mempelajari ilmu mengenai pengembangan sistem eLearning yang telah diterapkan di Universitas Telkom. Kegiatan ini diawali dengan pemutaran video profil Universitas Telkom dan CeLOE kemudian dilanjutkan dengan sharing session oleh Pusdikat Tenaga Teknis Pendidikan dan Keagamaan mengenai perlunya inovasi yang mendukung pembelajaran dan pengembangan kompetensi. Hal ini dianggap sangat penting karena LMS yang telah dibangun dikatakan kurang maksimal. Menanggapi hal tersebut tim CeLOE memberikan gambaran dengan memaparkan proses berlangsungnya pembelajaran blended learning, khususnya terkait kebutuhan software, hardware, teknis dasar, konten, Course Development System (CDS), Learning Management System (LMS) dan lain sebagainya. Setelah sharing session berakhir, kegiatan selanjutnya yaitu benchmarking ke studio-studio CeLOE dan diakhiri dengan pemberian cindera mata serta foto bersama. Kegiatan ini diharapkan mampu memberikan inspirasi dan menambah wawasan untuk mewujudkan pendidikan yang lebih berkualitas</p>', 'Dokumentasi', '2023-03-27 00:23:27', '2023-03-27 00:23:27');

